void
SPISDataPut(uint32_t ui32Data)
{
    //
    // Wait until there is space.
    //
    while(HWREG(SPIS_BASE + SPIS_O_TXFSTAT) & SPIS_TXFSTAT_FULL)
    {
    }

    //
    // Write the data to the SPIS Tx Fifo.
    //
    HWREG(SPIS_BASE + SPIS_O_TXFPUSH) = ui32Data;
}
