static uint8_t ndef_mem_logo[sizeof(riot_logo) + 1024];
static uint8_t t2t_mem_logo[sizeof(riot_logo) + 1024];

static const uint8_t json_data[] = "{ \"RIOT\" : 1, \"OS\" : 2}";
static uint8_t ndef_mem_json[sizeof(json_data) + 64];
static uint8_t t2t_mem_json[sizeof(json_data) + 64];

#define BUFFER_SIZE 1024

static uint8_t ndef_mem[BUFFER_SIZE];
static uint8_t t2t_mem[BUFFER_SIZE];

static bool test_nfct(void)
{
    puts("Starting NFC T2T test");
    ndef_t ndef_message;

    ndef_init(&ndef_message, ndef_mem, 1024);
    ndef_add_text_record(&ndef_message, "Hello World", 11, "en", 2, UTF8);
    ndef_add_uri_record(&ndef_message, NDEF_URI_HTTPS_WWW, "riot-os.org", 11);

    nfc_t2t_t t2t;
    t2t_create_type_2_tag(&t2t, NULL, NULL, NULL, NFC_T2T_STATIC_MEMORY_SIZE, t2t_mem);
    nfct_start_type_2_tag_with_ndef(&DEFAULT_T2T_EMULATOR_DEV, &t2t, &ndef_message);
    /* sleep for 10 seconds, then disable the tag */
    ztimer_sleep(ZTIMER_SEC, 10);
    nfct_stop_type_2_tag(&DEFAULT_T2T_EMULATOR_DEV);
    return true;
}

static bool test_convenience_functions(void)
{
    puts("Starting convenience tests");
    nfc_t2t_t t2t;
    nfct_start_type_2_tag_with_text(&DEFAULT_T2T_EMULATOR_DEV, &t2t, t2t_mem, BUFFER_SIZE, "Hallo Welt", "de", UTF8);
    /* sleep for 10 seconds, then disable the tag */
    ztimer_sleep(ZTIMER_SEC, 10);
    nfct_stop_type_2_tag(&DEFAULT_T2T_EMULATOR_DEV);

    // run a test for long content > 0xFF byte
    static char *content = "Sed ut perspiciatis unde omnis iste natus error sit voluptatem "
                           "accusantium doloremque laudantium, totam rem aperiam, eaque ipsa quae ab illo "
                           "inventore veritatis et quasi architecto beatae vitae dicta sunt explicabo. "
                           "Nemo enim ipsam voluptatem quia voluptas sit\0";
    nfct_start_type_2_tag_with_text(&DEFAULT_T2T_EMULATOR_DEV, &t2t, t2t_mem, BUFFER_SIZE, content, "de", UTF8);
    ztimer_sleep(ZTIMER_SEC, 10);
    nfct_stop_type_2_tag(&DEFAULT_T2T_EMULATOR_DEV);

    nfct_start_type_2_tag_with_uri(&DEFAULT_T2T_EMULATOR_DEV, &t2t, t2t_mem, BUFFER_SIZE, NDEF_URI_HTTPS_WWW, "riot-os.org");
    ztimer_sleep(ZTIMER_SEC, 10);
    nfct_stop_type_2_tag(&DEFAULT_T2T_EMULATOR_DEV);
    return 0;
}

static bool test_riot_logo(void)
{
    // array size is 107040+
    ndef_t ndef_message;
    ndef_init(&ndef_message, ndef_mem_logo, sizeof(ndef_mem_logo));

    ndef_add_mime_record(&ndef_message, "image/png", 9,
                         riot_logo, sizeof(riot_logo));

    nfc_t2t_t t2t;
    t2t_create_type_2_tag(&t2t, NULL, NULL, NULL, sizeof(riot_logo) + 30, t2t_mem_logo);

    nfct_start_type_2_tag(&DEFAULT_T2T_EMULATOR_DEV, &t2t);

    ztimer_sleep(ZTIMER_SEC, 10);

    nfct_stop_type_2_tag(&DEFAULT_T2T_EMULATOR_DEV);

    return true;
}

static bool test_json(void)
{
    puts("Testing JSON");
    ndef_t ndef_message;
    ndef_init(&ndef_message, ndef_mem_json, sizeof(ndef_mem_json));

    ndef_add_mime_record(&ndef_message, "application/json", 16,
                         json_data, sizeof(json_data));

    nfc_t2t_t t2t;
    t2t_create_type_2_tag(&t2t, NULL, NULL, NULL, sizeof(json_data) + 30, t2t_mem_json);

    nfct_start_type_2_tag(&DEFAULT_T2T_EMULATOR_DEV, &t2t);

    ztimer_sleep(ZTIMER_SEC, 10);

    nfct_stop_type_2_tag(&DEFAULT_T2T_EMULATOR_DEV);

    return true;
}

int main(void)
{
    puts("Starting NFC tests");
    test_json();
    test_riot_logo();
    test_nfct();
    test_convenience_functions();
    puts("Ending NFC tests");
    return 0;
}
